import React, {useEffect, useState} from 'react';
import {v4 as uuidv4} from 'uuid'
import axios from "axios";
import ListUsers from "../ListUsers/ListUsers";

const UsersRequest = ({textValue, boolMale}) =>{
    const [state, setState] = useState({
        allUsers: [],
    })
    useEffect(() =>{
        try {
            const response = axios.get('https://venbest-test.herokuapp.com/');
            response.then(response => setState({
                allUsers: [...response.data]
            }));
        }catch (error) {
            console.log(error)
        }

        }, []);
    const results = state.allUsers.map((users) => {
        const str = String(users.age);
        if(users.name.includes(textValue) || users.lastname.includes(textValue) || str.includes(textValue)){
            return(
                <ListUsers name={users.name} lastname={users.lastname}/>
            )
        }
        if(boolMale){
            const a = state.allUsers.filter(users => users.sex === 'm');
            return  <ListUsers name={a.name} />
        }
    })





    return(
        <div>
            {results}
        </div>
    )

}

export default UsersRequest;
