import React, {useEffect, useState} from 'react';
import {v4 as uuidv4} from 'uuid'
import axios from "axios";

const UsersRequest = ({textValue, boolMale}) =>{
    const [state, setState] = useState({
        allUsers: [],
    })
    useEffect(() =>{
        try {
            const response = axios.get('https://venbest-test.herokuapp.com/');
            response.then(response => setState({
                allUsers: [...response.data]
            }));
        }catch (error) {
            console.log(error)
        }

        }, []);
    const results = state.allUsers.map((users) =>{
        if((users.name.indexOf(textValue) && users.lastname.indexOf(textValue)) !== -1){
            return (
                <ul>
                    <li>{users.name}</li>
                </ul>
            )
        }else if(boolMale){
            const bool = users.filter(item => item.sex === 'm');
            return (
                <ul>
                    <li>{bool.name}</li>
                </ul>
            )
        }
    })

    return(
        <div>
            {results}

        </div>
    )

}

export default UsersRequest;
