import {v4 as uuidv4} from 'uuid'
import axios from "axios";

const UsersRequest = () =>{
    const [state, setState] = useState([])
    const [search, setSearch] = useState('')
    const [filter, setFilter] = useState([])

    useEffect(() =>{
        try {
            const response = axios.get('https://venbest-test.herokuapp.com/');
            response.then(response => setState(response.data));
        }catch (error) {
            console.log(error)
        }

        }, []);

    const handelChange = (event) =>{
        setSearch(event.target.value)
    }
    const results = state.map(({name, lastname, age, sex}) =>{
        if(name.indexOf(search) === -1){
            return;
        }
        setFilter([name])

    })


    const users = state.map(({name, lastname, age, sex}) => {
        return(
            <ul key={uuidv4()}>
                <li>{name} {lastname}  {age}  {sex}</li>
            </ul>
        )
    })



    return(
        <div>
            <div>
                <form action="">
                    <input type="text" placeholder='Введите имя' onChange={handelChange}/>
                </form>
            </div>


        </div>
    )

}

export default UsersRequest;
